            </div>
          </div>

          {/* SecÈ›iunea cu feedback-ul AI */}
          {feedback && (
            <div className="feedback-section">
              <h3 style={{ ...(isAccessibilityMode ? { tabIndex: -1 } : {}) }}>Feedback AI:</h3>
              <div 
                className="feedback-content"
