import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useToast } from './ToastProvider';
import '../styles/Quiz.css';

function Quiz() {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState([]);
  const [score, setScore] = useState(0);
  const [isFinished, setIsFinished] = useState(false);
  const [isAnswerChecked, setIsAnswerChecked] = useState(false);
  const [courseId, setCourseId] = useState(null);
  const [totalLessons, setTotalLessons] = useState(0);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const toast = useToast();

  // Obține userId din localStorage
  const userId = localStorage.getItem('userId');
  const authToken = localStorage.getItem('authToken');

  useEffect(() => {
    const courseIdFromUrl = window.location.pathname.split('/')[2];
    setCourseId(courseIdFromUrl);

    // Verificare autentificare
    if (!userId || !authToken) {
      console.error('Utilizatorul nu este autentificat');
      setError('Trebuie să fii autentificat pentru a susține testul.');
      toast.error('Trebuie să fii autentificat pentru a susține testul.');
      return;
    }

    // Anunță utilizatorul că testul a fost încărcat
    toast.info('Testul se încarcă...', 2000);

    fetch(`http://localhost:5000/api/courses/${courseIdFromUrl}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.course && data.course.questions) {
          setQuestions(data.course.questions);
          setTotalLessons(data.course.totalLessons);
          
          // Notificare când testul este gata
          toast.success('Testul a fost încărcat. Succes!', 3000);
        }
      })
      .catch((error) => {
        console.error('Error fetching questions:', error);
        setError('Nu am putut încărca întrebările pentru test.');
        toast.error('Nu am putut încărca întrebările pentru test.');
      });
  }, [userId, authToken, toast]);

  const handleAnswerChange = useCallback((index, answerIndex) => {
    const updatedAnswers = [...selectedAnswers];
    updatedAnswers[index] = answerIndex;
    setSelectedAnswers(updatedAnswers);
  }, [selectedAnswers]);

  // Funcție pentru salvarea progresului la test
  const saveProgress = useCallback(async (testScore) => {
    if (!userId || !courseId) {
      console.error('Lipsesc informații esențiale: userId sau courseId');
      toast.error('Nu se poate salva progresul - lipsesc informații esențiale');
      return;
    }

    setSaving(true);
    toast.info('Se salvează rezultatul testului...', 2000);
    
    try {
      // Obținem progresul existent pentru acest curs
      const progressResponse = await fetch(`http://localhost:5000/api/user/progress/${userId}`);
      const progressData = await progressResponse.json();
      
      // Găsim progresul pentru cursul curent
      const currentProgress = progressData.find(p => p.courseId._id === courseId);
      
      // Determinăm câte lecții au fost completate (păstrăm valoarea existentă)
      const completedLessons = currentProgress ? currentProgress.completedLessons : totalLessons;

      // Salvăm progresul actualizat
      const response = await fetch('http://localhost:5000/api/user/progress', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}`
        },
        body: JSON.stringify({
          userId,
          courseId,
          completedLessons,
          totalLessons,
          testScore,
        }),
      });
      
      const data = await response.json();
      
      if (!data.success) {
        throw new Error(data.message || 'Eroare la salvarea progresului');
      }
      
      toast.success('Rezultatul testului a fost salvat cu succes!');
      console.log('Progresul la test salvat cu succes');
    } catch (error) {
      console.error('Eroare la salvarea progresului testului:', error);
      setError('Nu am putut salva progresul. Te rugăm să încerci din nou.');
      toast.error('Eroare la salvarea rezultatului. Încearcă din nou.');
    } finally {
      setSaving(false);
    }
  }, [userId, courseId, totalLessons, authToken, toast]);

  const handleNext = useCallback(() => {
    if (isAnswerChecked) {
      // Al doilea click - trecem la următoarea întrebare
      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setIsAnswerChecked(false); // Resetăm starea
        toast.info(`Întrebarea ${currentQuestionIndex + 2} din ${questions.length}`, 1500);
      } else {
        // La finalizarea testului, calculăm scorul final și salvăm progresul
        const finalScore = Math.round((score / questions.length) * 100);
        saveProgress(finalScore);
        
        // Feedback personalizat în funcție de scor
        if (finalScore === 100) {
          toast.success('Perfect! Ai obținut scor maxim! 🏆', 5000);
        } else if (finalScore >= 80) {
          toast.success(`Excelent! Ai obținut ${finalScore}% la test! 🎉`, 5000);
        } else if (finalScore >= 60) {
          toast.info(`Bun rezultat! Ai obținut ${finalScore}% la test. 👍`, 5000);
        } else {
          toast.warning(`Ai obținut ${finalScore}%. Poate ar trebui să revezi lecțiile. 📚`, 5000);
        }
        
        setIsFinished(true);
      }
    } else {
      // Primul click - validăm răspunsul
      if (selectedAnswers[currentQuestionIndex] === undefined) {
        toast.warning('Te rugăm să selectezi un răspuns!');
        return;
      }

      // Verificăm dacă răspunsul este corect
      if (selectedAnswers[currentQuestionIndex] === questions[currentQuestionIndex].correctAnswerIndex) {
        setScore(prevScore => prevScore + 1); // Actualizăm scorul
        toast.success('Răspuns corect! +1 punct', 1500);
      } else {
        toast.error('Răspuns greșit!', 1500);
      }

      setIsAnswerChecked(true); // Setăm că răspunsul a fost verificat
    }
  }, [isAnswerChecked, currentQuestionIndex, questions, selectedAnswers, score, saveProgress, toast]);

  const handleRestart = useCallback(() => {
    setScore(0);
    setCurrentQuestionIndex(0);
    setSelectedAnswers([]);
    setIsFinished(false);
    setIsAnswerChecked(false); // Resetăm starea pentru validarea răspunsurilor
    toast.info('Ai reînceput testul. Mult succes!', 3000);
  }, [toast]);

  const handleBackToProfile = useCallback(() => {
    toast.info('Te-ai întors la profilul tău', 2000);
    navigate('/profile');
  }, [navigate, toast]);

  if (error) {
    return (
      <div className="quiz-error">
        <h2>Eroare la încărcarea testului</h2>
        <p>{error}</p>
        <button onClick={() => navigate(-1)}>Înapoi</button>
      </div>
    );
  }

  if (questions.length === 0) {
    return <div className="quiz-loading">Se încarcă întrebările...</div>;
  }

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="quiz-container">
      <h2>Întrebarea {currentQuestionIndex + 1}</h2>

      {!isFinished ? (
        <div className="question-container">
          <h3>{currentQuestion.question}</h3>
          <ul>
            {currentQuestion.options.map((option, index) => {
              let className = '';
              if (selectedAnswers[currentQuestionIndex] === index) {
                // Adaugă clasa selected la răspunsul ales
                className = 'selected';
              }
              if (isAnswerChecked) {
                if (index === questions[currentQuestionIndex].correctAnswerIndex) {
                  // Răspuns corect - verde
                  className = 'correct';
                } else if (selectedAnswers[currentQuestionIndex] === index) {
                  // Răspuns greșit - roșu
                  className = 'incorrect';
                }
              }
              return (
                <li
                  key={index}
                  className={className}
                  onClick={() => handleAnswerChange(currentQuestionIndex, index)} // Activăm click pe întreaga listă
                >
                  <input
                    type="radio"
                    id={`question-${currentQuestionIndex}-option-${index}`}
                    name={`question-${currentQuestionIndex}`}
                    value={index}
                    checked={selectedAnswers[currentQuestionIndex] === index}
                    onChange={() => {}} // Manipulăm schimbarea prin onClick pe li
                  />
                  <label htmlFor={`question-${currentQuestionIndex}-option-${index}`}>{option}</label>
                </li>
              );
            })}
          </ul>
          <button 
            onClick={handleNext}
            disabled={saving}
          >
            {isAnswerChecked ? 'Next' : 'Verifică Răspunsul'}
          </button>
        </div>
      ) : (
        <div className="result-container">
          <h3>Testul S-a Încheiat</h3>
          <p>Punctajul tău: {score} din {questions.length}</p>
          <p className="score-percentage">Ai obținut {Math.round((score / questions.length) * 100)}%</p>
          
          <div className="result-actions">
            <button onClick={handleRestart}>Reia Testul</button>
            <button onClick={handleBackToProfile}>Vizualizează Profilul</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Quiz;
