// src/components/AccessibleCodePlayground.js - Fixed example navigation
import React, { useState, useEffect } from 'react';

function AccessibleCodePlayground() {
  const [selectedExampleIndex, setSelectedExampleIndex] = useState(0);
  const [isInEditor, setIsInEditor] = useState(false);
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('Apasă "Execută Codul" pentru a vedea rezultatul...');
  const [hasAnnouncedExamples, setHasAnnouncedExamples] = useState(false);

  // Exemple de cod predefinite
  const codeExamples = [
    {
      title: "Hello World",
      description: "Primul tău program Java - afișează un salut simplu",
      code: `public class HelloWorld {
    public static void main(String[] args) {
        System.out.println("Hello, World!");
    }
}`
    },
    {
      title: "Variabile și Tipuri", 
      description: "Învață să declari și să folosești variabile în Java",
      code: `public class Variables {
    public static void main(String[] args) {
        int age = 25;
        String name = "Java Developer";
        double salary = 5000.50;
        boolean isEmployed = true;
        
        System.out.println("Nume: " + name);
        System.out.println("Vârsta: " + age);
        System.out.println("Salar: " + salary);
        System.out.println("Angajat: " + isEmployed);
    }
}`
    },
    {
      title: "Structuri Condiționale",
      description: "Folosește if-else pentru a lua decizii în cod", 
      code: `public class Conditionals {
    public static void main(String[] args) {
        int score = 85;
        
        if (score >= 90) {
            System.out.println("Nota: A");
        } else if (score >= 80) {
            System.out.println("Nota: B");
        } else if (score >= 70) {
            System.out.println("Nota: C");
        } else {
            System.out.println("Nota: F");
        }
    }
}`
    },
    {
      title: "Bucle",
      description: "Repetă operații cu for și while",
      code: `public class Loops {
    public static void main(String[] args) {
        // Bucla for
        System.out.println("Numerele de la 1 la 5:");
        for (int i = 1; i <= 5; i++) {
            System.out.println(i);
        }
        
        // Bucla while
        System.out.println("Countdown:");
        int countdown = 3;
        while (countdown > 0) {
            System.out.println(countdown);
            countdown--;
        }
        System.out.println("Start!");
    }
}`
    },
    {
      title: "Arrays",
      description: "Lucrează cu colecții de elemente",
      code: `public class Arrays {
    public static void main(String[] args) {
        String[] fruits = {"măr", "banană", "portocală", "strugure"};
        
        System.out.println("Fructele mele preferate:");
        for (int i = 0; i < fruits.length; i++) {
            System.out.println((i + 1) + ". " + fruits[i]);
        }
        
        // Enhanced for loop
        System.out.println("\\nCu enhanced for:");
        for (String fruit : fruits) {
            System.out.println("- " + fruit);
        }
    }
}`
    }
  ];

  // FIXAT: Încărcarea unui exemplu în editor
  const loadExampleInEditor = (example) => {
    setCode(example.code);
    setIsInEditor(true);
    
    if (window.accessibilityManager?.isActive && window.accessibilityManager?.speak) {
      window.accessibilityManager.speak(
        `Exemplul "${example.title}" a fost încărcat în editor. Modul editare activat. Poți scrie și modifica codul acum.`,
        'high'
      );
    }
  };

  // Ieșirea din editor
  const exitEditor = () => {
    setIsInEditor(false);
    
    if (window.accessibilityManager?.isActive && window.accessibilityManager?.speak) {
      window.accessibilityManager.speak(
        'Ai ieșit din editor. Înapoi la lista de exemple. Folosește săgețile pentru navigare.',
        'high'
      );
    }
  };

  // Executarea codului
  const executeCode = () => {
    if (window.accessibilityManager?.isActive && window.accessibilityManager?.speak) {
      window.accessibilityManager.speak('Se execută codul...', 'high');
    }
    
    // Simulare execuție
    setTimeout(() => {
      const simulatedOutput = "Hello, World!\nCod executat cu succes!";
      setOutput(simulatedOutput);
      
      if (window.accessibilityManager?.isActive && window.accessibilityManager?.speak) {
        window.accessibilityManager.speak(`Codul a fost executat. Output: ${simulatedOutput}`, 'high');
      }
    }, 1000);
  };

  // Handler pentru schimbarea codului în editor
  const handleCodeChange = (e) => {
    setCode(e.target.value);
  };

  // FIXAT: Listener pentru comenzi cu tastatura
  useEffect(() => {
    const handleKeyPress = (e) => {
      // Verificăm dacă accessibility manager este disponibil
      const isAccessibilityMode = window.accessibilityManager?.isActive || false;
      
      if (!isAccessibilityMode) return;

      // FIXAT: În editor, doar Escape pentru a ieși
      if (isInEditor) {
        if (e.key === 'Escape') {
          e.preventDefault();
          e.stopPropagation();
          exitEditor();
        }
        return; // Nu procesăm alte taste în editor
      }

      // FIXAT: Navigare prin exemple cu săgețile DOAR când nu suntem în editor
      if (e.key === 'ArrowLeft') {
        e.preventDefault();
        e.stopPropagation();
        const newIndex = selectedExampleIndex > 0 ? selectedExampleIndex - 1 : codeExamples.length - 1;
        setSelectedExampleIndex(newIndex);
        
        if (window.accessibilityManager?.speak) {
          const example = codeExamples[newIndex];
          window.accessibilityManager.speak(
            `Exemplul ${newIndex + 1} din ${codeExamples.length}: ${example.title}. ${example.description}. Apasă Enter pentru a încărca în editor.`,
            'high'
          );
        }
      } else if (e.key === 'ArrowRight') {
        e.preventDefault();
        e.stopPropagation();
        const newIndex = selectedExampleIndex < codeExamples.length - 1 ? selectedExampleIndex + 1 : 0;
        setSelectedExampleIndex(newIndex);
        
        if (window.accessibilityManager?.speak) {
          const example = codeExamples[newIndex];
          window.accessibilityManager.speak(
            `Exemplul ${newIndex + 1} din ${codeExamples.length}: ${example.title}. ${example.description}. Apasă Enter pentru a încărca în editor.`,
            'high'
          );
        }
      }
      
      // Enter pentru a încărca exemplul în editor
      if (e.key === 'Enter') {
        e.preventDefault();
        e.stopPropagation();
        loadExampleInEditor(codeExamples[selectedExampleIndex]);
      }
    };

    // FIXAT: Adăugăm listener cu capture pentru prioritate
    document.addEventListener('keydown', handleKeyPress, true);
    return () => document.removeEventListener('keydown', handleKeyPress, true);
  }, [selectedExampleIndex, isInEditor, codeExamples]);

  // FIXAT: Anunț la încărcarea paginii - doar o dată
  useEffect(() => {
    if (window.accessibilityManager?.isActive && window.accessibilityManager?.speak && !hasAnnouncedExamples) {
      setTimeout(() => {
        window.accessibilityManager.speak(
          `Playground de cod Java încărcat. Sunt disponibile ${codeExamples.length} exemple. Folosește săgețile stânga și dreapta pentru navigare prin exemple.`,
          'high'
        );
        
        setTimeout(() => {
          const firstExample = codeExamples[0];
          window.accessibilityManager.speak(
            `Exemplul 1 din ${codeExamples.length}: ${firstExample.title}. ${firstExample.description}. Apasă Enter pentru a încărca în editor.`,
            'high'
          );
        }, 2000);
        
        setHasAnnouncedExamples(true);
      }, 1000);
    }
  }, [codeExamples, hasAnnouncedExamples]);

  const isAccessibilityMode = window.accessibilityManager?.isActive || false;

  return (
    <div className="code-playground-container">
      {isAccessibilityMode && (
        <div className="accessibility-instructions" style={{ 
          position: 'absolute', 
          left: '-9999px',
          width: '1px',
          height: '1px' 
        }}>
          <p>
            {isInEditor 
              ? "Playground de cod Java - Editor activ. Scrie codul tău aici. Escape pentru a ieși din editor."
              : "Playground de cod Java. Navighează prin exemple cu săgețile stânga și dreapta. Enter pentru a încărca exemplul în editor."
            }
          </p>
        </div>
      )}

      <h1>Java Code Playground</h1>
      <p className="playground-description">
        Experimentează cu cod Java folosind exemplele de mai jos sau scrie propriul tău cod.
      </p>

      {!isInEditor ? (
        // FIXAT: Secțiunea cu exemple - nu întrăm direct în editor
        <div className="examples-section">
          <h2 style={{ ...(isAccessibilityMode ? { tabIndex: -1 } : {}) }}>Exemple de cod</h2>
          
          {isAccessibilityMode && (
            <div className="accessibility-navigation-info" style={{ 
              textAlign: 'center', 
              padding: '20px', 
              backgroundColor: '#f0f0f0', 
              borderRadius: '8px', 
              margin: '20px 0',
              border: '2px solid #2196F3'
            }}>
              <p>
                <strong>Exemplul curent: {selectedExampleIndex + 1} din {codeExamples.length}</strong><br />
                <strong>{codeExamples[selectedExampleIndex]?.title}</strong><br />
                Folosește săgețile pentru navigare și Enter pentru încărcare în editor.
              </p>
            </div>
          )}
          
          <div className="examples-grid" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px', margin: '20px 0' }}>
            {codeExamples.map((example, index) => (
              <div 
                key={index}
                className={`example-card ${isAccessibilityMode && index === selectedExampleIndex ? 'accessibility-focus' : ''}`}
                onClick={() => loadExampleInEditor(example)}
                role={isAccessibilityMode ? "button" : undefined}
                aria-label={isAccessibilityMode ? `Exemplul ${index + 1}: ${example.title}. ${example.description}` : undefined}
                tabIndex={isAccessibilityMode ? (index === selectedExampleIndex ? 0 : -1) : 0}
                style={{
                  border: '1px solid #ddd',
                  borderRadius: '8px',
                  padding: '20px',
                  cursor: 'pointer',
                  backgroundColor: isAccessibilityMode && index === selectedExampleIndex ? '#e3f2fd' : '#f9f9f9',
                  transition: 'all 0.3s ease',
                  outline: isAccessibilityMode && index === selectedExampleIndex ? '3px solid #ffff00' : 'none'
                }}
              >
                <h3 className="example-title" style={{ 
                  margin: '0 0 10px 0', 
                  color: '#333',
                  ...(isAccessibilityMode ? { tabIndex: -1 } : {})
                }}>
                  {example.title}
                </h3>
                <p className="example-description" style={{ 
                  margin: '0 0 15px 0', 
                  color: '#666', 
                  fontSize: '14px',
                  ...(isAccessibilityMode ? { tabIndex: -1 } : {})
                }}>
                  {example.description}
                </p>
                <div className="example-code-preview" style={{ 
                  backgroundColor: '#f5f5f5', 
                  padding: '10px', 
                  borderRadius: '4px', 
                  fontSize: '12px',
                  ...(isAccessibilityMode ? { tabIndex: -1 } : {})
                }}>
                  <code>{example.code.substring(0, 100)}...</code>
                </div>
                {!isAccessibilityMode && (
                  <button 
                    className="load-example-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      loadExampleInEditor(example);
                    }}
                    style={{
                      marginTop: '15px',
                      backgroundColor: '#2196F3',
                      color: 'white',
                      border: 'none',
                      padding: '8px 16px',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    Încarcă în Editor
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>
      ) : (
        // Secțiunea cu editorul
        <div className="editor-section">
          <h2 style={{ ...(isAccessibilityMode ? { tabIndex: -1 } : {}) }}>Editor de cod</h2>
          <div className="editor-container">
            <textarea
              value={code}
              onChange={handleCodeChange}
              className="simple-code-editor"
              placeholder="Scrie codul tău Java aici..."
              rows={20}
              cols={80}
              autoFocus={true} // Focus automat în editor
              style={{
                fontFamily: 'Consolas, Monaco, "Courier New", monospace',
                fontSize: '14px',
                border: '1px solid #ccc',
                padding: '15px',
                width: '100%',
                resize: 'vertical',
                backgroundColor: '#f8f8f8',
                borderRadius: '4px',
                lineHeight: '1.5'
              }}
            />
          </div>
          
          <div className="editor-actions" style={{ margin: '20px 0' }}>
            <button 
              onClick={executeCode}
              className="execute-btn"
              aria-label={isAccessibilityMode ? "Execută codul din editor" : undefined}
              style={{
                backgroundColor: '#4CAF50',
                color: 'white',
                border: 'none',
                padding: '12px 24px',
                marginRight: '10px',
                cursor: 'pointer',
                borderRadius: '4px',
                fontSize: '16px'
              }}
            >
              🚀 Execută Codul
            </button>
            
            <button 
              onClick={exitEditor}
              className="back-to-examples-btn"
              aria-label={isAccessibilityMode ? "Înapoi la lista de exemple" : undefined}
              style={{
                backgroundColor: '#2196F3',
                color: 'white', 
                border: 'none',
                padding: '12px 24px',
                cursor: 'pointer',
                borderRadius: '4px',
                fontSize: '16px'
              }}
            >
              📚 Înapoi la Exemple
            </button>
          </div>
          
          <div className="output-section">
            <h3 style={{ ...(isAccessibilityMode ? { tabIndex: -1 } : {}) }}>Output</h3>
            <div className="output-console" style={{
              backgroundColor: '#2d2d2d',
              color: '#ffffff',
              border: '1px solid #555',
              padding: '15px',
              fontFamily: 'Consolas, Monaco, "Courier New", monospace',
              whiteSpace: 'pre-wrap',
              minHeight: '120px',
              borderRadius: '4px',
              fontSize: '14px',
              ...(isAccessibilityMode ? { tabIndex: -1 } : {})
            }}>
              {output}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AccessibleCodePlayground;
