import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/CourseDetailPage.css';

function CourseDetailPage() {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showContent, setShowContent] = useState(false); // Control pentru a extinde lecțiile

  useEffect(() => {
    // Fetch pentru a obține detaliile cursului pe baza ID-ului
    fetch(`http://localhost:5000/api/courses/${courseId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.course) {
          setCourse(data.course);  // Setăm cursul în stare
          setLoading(false);        // Setăm loading pe false
        } else {
          setLoading(false);        // Dacă nu s-a găsit cursul, oprim loading
        }
      })
      .catch(() => setLoading(false)); // Dacă există eroare la fetch
  }, [courseId]);

  if (loading) return <div>Loading...</div>;  // Dacă se încarcă cursul, arătăm Loading...
  if (!course) return <div>Course not found.</div>;  // Dacă nu găsim cursul, afișăm mesajul "not found"

  return (
    <div className="course-detail-page">
      <h1 className="course-detail-title">{course.name}</h1>
      <p className="course-detail-description">{course.description}</p>

      <h3 className="course-detail-content-title">Course Content:</h3>
      <ul className="course-detail-content-list">
        {course.content.map((lesson, index) => (
          <li key={index} className="course-detail-content-item">
            {/* Afișăm lecția fără a adăuga "Lesson X:" */}
            <h4>{lesson.title}</h4>
            <p>{lesson.shortDescription || "Short description of the lesson"}</p>

            {showContent && (
              <div className="course-lesson-text">
                <p>
                  {/* Aici poți adăuga detalii suplimentare pentru lecție */}
                  This is the full content for "{lesson.title}". <br />
                  Here you can add more details about the lesson, code examples, explanations, etc.
                </p>
              </div>
            )}
          </li>
        ))}
      </ul>

      <div className="course-buttons">
        {/* Buton pentru întoarcerea la lista de cursuri */}
        <button className="back-to-courses-btn" onClick={() => navigate('/courses')}>
          Back to Courses
        </button>

        {/* Buton pentru a începe cursul (extinde lecțiile) */}
        <button
          className="start-course-btn"
          onClick={() => setShowContent(!showContent)} // Toggle pentru a extinde lecțiile
        >
          {showContent ? 'Hide Content' : 'Start Course'}
        </button>
      </div>
    </div>
  );
}

export default CourseDetailPage;
