// src/components/Login.js - Fixed tab navigation for accessibility
import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useToast } from './ToastProvider';
import { useAccessibility } from '../hooks/useAccessibility';
import '../styles/FormPage.css';

function Login({ setIsAuthenticated }) {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const toast = useToast();
  
  // Refs pentru focus management în modul accesibilitate
  const emailRef = useRef(null);
  const passwordRef = useRef(null);
  const submitRef = useRef(null);
  
  // Adăugat suport pentru accesibilitate
  const { 
    isAccessibilityMode, 
    useNavigationAccessibility,
    announceFormError,
    announceFormSuccess,
    announceLoading 
  } = useAccessibility();
  
  const { announcePageLoad } = useNavigationAccessibility();

  // Anunțăm încărcarea paginii pentru utilizatorii cu accesibilitate
  useEffect(() => {
    if (isAccessibilityMode) {
      announcePageLoad(
        'Autentificare', 
        'Completează email-ul și parola pentru a te autentifica în JavaVerse.'
      );
      
      // Focus direct pe email în modul accesibilitate
      setTimeout(() => {
        if (emailRef.current) {
          emailRef.current.focus();
          announceLoading('Focus pe câmpul email. Introdu adresa de email.');
        }
      }, 1500);
    }
  }, [isAccessibilityMode, announcePageLoad, announceLoading]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validare simplă
    if (!formData.email || !formData.password) {
      const errorMessage = 'Te rugăm să completezi toate câmpurile.';
      toast.warning(errorMessage);
      if (isAccessibilityMode) {
        announceFormError('formular', errorMessage);
      }
      return;
    }
    
    setIsLoading(true);
    
    if (isAccessibilityMode) {
      announceLoading('Se procesează autentificarea...');
    }

    try {
      const response = await fetch('http://localhost:5000/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (data.success) {
        // Stocăm token-ul în sessionStorage
        sessionStorage.setItem('authToken', data.token);
        
        try {
          // Metoda simplă de extragere a userId din token
          const base64Url = data.token.split('.')[1];
          const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
          const jsonPayload = decodeURIComponent(atob(base64)
            .split('').map(c => '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2)).join(''));
          const decoded = JSON.parse(jsonPayload);
          
          console.log("Decoded token:", decoded);
          
          // Salvăm userId în sessionStorage
          sessionStorage.setItem('userId', decoded.id || decoded.userId);
          console.log("Saved userId:", decoded.id || decoded.userId);
        } catch (err) {
          console.error("Error decoding token:", err);
          // Ca soluție de rezervă, vom face o cerere pentru a obține userId-ul
          await fetchUserProfile(data.token);
        }
        
        // Setăm starea de autentificare
        setIsAuthenticated(true);
        
        // Afișăm un toast de succes
        const successMessage = 'Autentificare reușită! Bine ai venit!';
        toast.success(successMessage);
        if (isAccessibilityMode) {
          announceFormSuccess(successMessage);
        }
        
        // Persistăm modul de accesibilitate înainte de navigare
        if (isAccessibilityMode) {
          sessionStorage.setItem('accessibilityModeActive', 'true');
        }
        
        // Redirecționăm utilizatorul către pagina principală
        navigate('/');
      } else {
        // Afișăm un toast de eroare cu mesajul de la server
        const errorMessage = data.message || 'Autentificare eșuată. Verifică email-ul și parola.';
        toast.error(errorMessage);
        if (isAccessibilityMode) {
          announceFormError('autentificare', errorMessage);
        }
      }
    } catch (error) {
      console.error('Error during login:', error);
      // Afișăm un toast de eroare pentru eroarea de rețea
      const errorMessage = 'Eroare de rețea. Te rugăm să încerci din nou.';
      toast.error(errorMessage);
      if (isAccessibilityMode) {
        announceFormError('conexiune', errorMessage);
      }
    } finally {
      setIsLoading(false);
    }
  };

  // Funcție de rezervă pentru obținerea profilului și userId-ului
  const fetchUserProfile = async (token) => {
    try {
      // Facem o cerere pentru a obține profilul utilizatorului
      const response = await fetch('http://localhost:5000/api/users/profile', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const userData = await response.json();
        // Salvăm în sessionStorage
        sessionStorage.setItem('userId', userData.user._id);
        console.log("Fetched and saved userId:", userData.user._id);
      }
    } catch (err) {
      console.error("Error fetching user profile:", err);
      if (isAccessibilityMode) {
        announceFormError('profil', 'Autentificare reușită, dar există probleme la obținerea profilului.');
      } else {
        toast.warning('Autentificare reușită, dar există probleme la obținerea profilului.');
      }
    }
  };

  // Handler pentru navigarea cu tastatura în modul accesibilitate
  const handleKeyDown = (e) => {
    if (!isAccessibilityMode) return;
    
    // Override pentru Tab navigation
    if (e.key === 'Tab') {
      e.preventDefault();
      
      if (document.activeElement === emailRef.current) {
        // Dacă suntem în modul editare, ieșim din el
        if (window.accessibilityManager && window.accessibilityManager.isEditMode) {
          window.accessibilityManager.exitEditMode();
        }
        
        if (!e.shiftKey) {
          // Tab normal: email -> password
          passwordRef.current.focus();
          announceLoading('Focus pe câmpul parolă.');
        }
      } else if (document.activeElement === passwordRef.current) {
        // Dacă suntem în modul editare, ieșim din el
        if (window.accessibilityManager && window.accessibilityManager.isEditMode) {
          window.accessibilityManager.exitEditMode();
        }
        
        if (e.shiftKey) {
          // Shift+Tab: password -> email
          emailRef.current.focus();
          announceLoading('Focus pe câmpul email.');
        } else {
          // Tab normal: password -> submit button
          submitRef.current.focus();
          announceLoading('Focus pe butonul de autentificare.');
        }
      } else if (document.activeElement === submitRef.current) {
        if (e.shiftKey) {
          // Shift+Tab: submit -> password
          passwordRef.current.focus();
          announceLoading('Focus pe câmpul parolă.');
        } else {
          // Tab normal: submit -> email (wrap around)
          emailRef.current.focus();
          announceLoading('Focus pe câmpul email.');
        }
      }
    }
    
    // Enter pentru a intra în modul editare sau a submite
    if (e.key === 'Enter') {
      e.preventDefault();
      
      if (document.activeElement === emailRef.current) {
        // Intrăm în modul editare pentru email
        if (window.accessibilityManager) {
          window.accessibilityManager.enterEditMode(emailRef.current);
        }
      } else if (document.activeElement === passwordRef.current) {
        // Intrăm în modul editare pentru parolă
        if (window.accessibilityManager) {
          window.accessibilityManager.enterEditMode(passwordRef.current);
        }
      } else if (document.activeElement === submitRef.current) {
        // Submit form
        handleSubmit(e);
      }
    }
  };

  return (
    <div className="form-container">
      {isAccessibilityMode && (
        <div className="accessibility-instructions" style={{ 
          position: 'absolute', 
          left: '-9999px',
          width: '1px',
          height: '1px' 
        }}>
          <p>
            Formular de autentificare. Completează email-ul și parola, apoi apasă Enter sau butonul Autentificare.
          </p>
        </div>
      )}
      
      <h1>Autentificare</h1>
      
      <form onSubmit={handleSubmit} onKeyDown={handleKeyDown}>
        <div className="input-group">
          <label htmlFor="email">Email</label>
          <input
            ref={emailRef}
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            autoComplete="email"
            aria-label={isAccessibilityMode ? "Adresa de email pentru autentificare" : undefined}
            aria-describedby={isAccessibilityMode ? "email-help" : undefined}
            tabIndex={isAccessibilityMode ? -1 : undefined} // Controlăm manual tab-ul
          />
          {isAccessibilityMode && (
            <div id="email-help" style={{ fontSize: '0.9rem', color: '#bbb', marginTop: '5px' }}>
              Introdu adresa de email asociată contului tău
            </div>
          )}
        </div>
        
        <div className="input-group">
          <label htmlFor="password">Parolă</label>
          <input
            ref={passwordRef}
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
            autoComplete="current-password"
            aria-label={isAccessibilityMode ? "Parola contului tău" : undefined}
            aria-describedby={isAccessibilityMode ? "password-help" : undefined}
            tabIndex={isAccessibilityMode ? -1 : undefined} // Controlăm manual tab-ul
          />
          {isAccessibilityMode && (
            <div id="password-help" style={{ fontSize: '0.9rem', color: '#bbb', marginTop: '5px' }}>
              Introdu parola contului tău
            </div>
          )}
        </div>
        
        <button 
          ref={submitRef}
          type="submit" 
          className="submit-button"
          disabled={isLoading}
          tabIndex={isAccessibilityMode ? -1 : undefined} // Controlăm manual tab-ul
          aria-label={isAccessibilityMode ? 
            (isLoading ? "Se procesează autentificarea..." : "Autentifică-te în JavaVerse") : 
            undefined
          }
        >
          {isLoading ? 'Se procesează...' : 'Autentificare'}
        </button>
      </form>
      
      <div className="switch-page">
        Nu ai un cont?{' '}
        <Link 
          to="/register"
          aria-label={isAccessibilityMode ? "Mergi la pagina de înregistrare pentru a crea un cont nou" : undefined}
          onClick={() => {
            if (isAccessibilityMode) {
              sessionStorage.setItem('accessibilityModeActive', 'true');
            }
          }}
        >
          Înregistrează-te
        </Link>
      </div>
    </div>
