import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from '../styles/Profile.module.css';

function Profile() {
  const [userProgress, setUserProgress] = useState([]);
  const [userInfo, setUserInfo] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('courses');
  const [error, setError] = useState(null);
  const [debugInfo, setDebugInfo] = useState(null); // Pentru debugging
  const navigate = useNavigate();
  
  // State pentru realizƒÉri
  const [achievements, setAchievements] = useState([]);
  const [loadingAchievements, setLoadingAchievements] = useState(false);
  const [newAchievements, setNewAchievements] = useState([]);

  useEffect(() => {
    // Func»õie pentru debugging - afi»ôeazƒÉ starea autentificƒÉrii
    const checkAuthStatus = () => {
      const authToken = localStorage.getItem('authToken');
      const userId = localStorage.getItem('userId');
      
      const debugData = {
        hasAuthToken: !!authToken,
        hasUserId: !!userId,
        authToken: authToken ? `${authToken.substring(0, 10)}...` : null,
        userId: userId || null
      };
      
      setDebugInfo(debugData);
      
      return { authToken, userId };
    };
    
    // VerificƒÉm starea autentificƒÉrii
    const { authToken, userId } = checkAuthStatus();
    
    if (!userId || !authToken) {
      // Nu redirec»õionƒÉm, doar afi»ôƒÉm eroare pentru debugging
      setError(`Autentificare necesarƒÉ. UserId: ${userId ? 'Prezent' : 'LipsƒÉ'}, Token: ${authToken ? 'Prezent' : 'LipsƒÉ'}`);
      setIsLoading(false);
      return;
    }
    
    // Func»õie pentru ob»õinerea informa»õiilor utilizatorului
    const fetchUserInfo = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/users/${userId}`);
        
        // Log pentru debugging
        console.log('User Info Response Status:', response.status);
        
        if (!response.ok) {
          throw new Error(`Failed to fetch user information. Status: ${response.status}`);
        }
        
        const data = await response.json();
        console.log('User Info Data:', data);
        
        if (data.success && data.user) {
          setUserInfo(data.user);
        } else {
          throw new Error(data.message || 'Error fetching user data');
        }
      } catch (error) {
        console.error('Error fetching user info:', error);
        setError(`Nu am putut ob»õine informa»õiile utilizatorului: ${error.message}`);
      }
    };
    
    // Func»õie pentru ob»õinerea progresului utilizatorului
    const fetchUserProgress = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/user/progress/${userId}`);
        
        // Log pentru debugging
        console.log('User Progress Response Status:', response.status);
        
        if (!response.ok) {
          throw new Error(`Failed to fetch user progress. Status: ${response.status}`);
        }
        
        const data = await response.json();
        console.log('User Progress Data:', data);
        
        setUserProgress(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error('Error fetching user progress:', error);
        setError(`Nu am putut ob»õine progresul utilizatorului: ${error.message}`);
      } finally {
        setIsLoading(false);
      }
    };
    
    // ApelƒÉm cele douƒÉ func»õii
    fetchUserInfo();
    fetchUserProgress();
    
    // IMPORTANT: Folosim date de test pentru realizƒÉri pentru a evita problemele API
    fetchTestAchievements();
  }, []);

  // Func»õie pentru a afi»ôa realizƒÉri de test
  const fetchTestAchievements = useCallback(() => {
    setLoadingAchievements(true);
    console.log("Folosim date de test temporare pentru realizƒÉri");
    
    // Date de test pentru realizƒÉri
    const testAchievements = [
      {
        _id: '1',
        userId: localStorage.getItem('userId'),
        name: 'Primul pas',
        description: 'Te-ai √Ænscris la primul curs',
        icon: 'üèÜ',
        category: 'course',
        isUnlocked: true,
        dateEarned: new Date().toISOString()
      },
      {
        _id: '2',
        userId: localStorage.getItem('userId'),
        name: 'Prima lec»õie',
        description: 'Ai completat prima lec»õie',
        icon: 'üìö',
        category: 'course',
        isUnlocked: true,
        dateEarned: new Date(Date.now() - 86400000).toISOString() // Ieri
      },
      {
        _id: '3',
        userId: localStorage.getItem('userId'),
        name: 'Explorator',
        description: 'Te-ai √Ænscris la 3 cursuri',
        icon: 'üß≠',
        category: 'course',
        isUnlocked: false,
        progress: { current: 1, target: 3 },
        dateEarned: null
      },
      {
        _id: '4',
        userId: localStorage.getItem('userId'),
        name: 'Student s√¢rguincios',
        description: 'Ai completat 10 lec»õii √Æn total',
        icon: 'üéì',
        category: 'course',
        isUnlocked: false,
        progress: { current: 3, target: 10 },
        dateEarned: null
      },
      {
        _id: '5',
        userId: localStorage.getItem('userId'),
        name: 'Test perfect',
        description: 'Ai ob»õinut 100% la un test',
        icon: 'üíØ',
        category: 'test',
        isUnlocked: false,
        progress: { current: 0, target: 1 },
        dateEarned: null
      }
    ];
    
    // SimulƒÉm un delay »ôi apoi setƒÉm datele
    setTimeout(() => {
      setAchievements(testAchievements);
      setLoadingAchievements(false);
    }, 500);
  }, []);

  // Func»õie pentru verificarea noilor realizƒÉri - doar simulare
  const checkNewAchievements = useCallback(() => {
    console.log("SimulƒÉm ob»õinerea unei noi realizƒÉri");
    
    // SimulƒÉm ob»õinerea unei noi realizƒÉri
    const newAchievement = {
      _id: '6',
      userId: localStorage.getItem('userId'),
      name: 'Primul test',
      description: 'Ai sus»õinut primul test',
      icon: 'üìù',
      category: 'test',
      isUnlocked: true,
      dateEarned: new Date().toISOString()
    };
    
    setNewAchievements([newAchievement]);
    
    // ActualizƒÉm lista de realizƒÉri
    setAchievements(prevAchievements => [newAchievement, ...prevAchievements]);
  }, []);

  // Func»õie pentru a naviga cƒÉtre un curs
  const handleContinueCourse = (courseId, lessonId = 0) => {
    navigate(`/courses/${courseId}/lesson/${lessonId}`);
  };

  // Func»õie pentru a naviga cƒÉtre pagina de cursuri
  const handleExploreCourses = () => {
    navigate('/courses');
  };

  // Func»õie pentru a calcula progresul √Æn procente
  const calculateProgress = (completedLessons, totalLessons) => {
    return (completedLessons / totalLessons) * 100;
  };

  // Func»õie pentru a determina clasa CSS pentru progres
  const getProgressColorClass = (progress) => {
    if (progress < 30) return styles.progressLow;
    if (progress < 70) return styles.progressMedium;
    return styles.progressHigh;
  };

  // Func»õie pentru a determina clasa CSS pentru scorul testului
  const getScoreClass = (score) => {
    if (score === null) return styles.scoreNone;
    if (score < 50) return styles.scoreLow;
    if (score < 80) return styles.scoreMedium;
    return styles.scoreHigh;
  };

  // Func»õie pentru calcularea procentului de progres pentru realizƒÉri
  const calculateAchievementProgress = (achievement) => {
    if (!achievement.progress) return 0;
    return Math.min(100, Math.round((achievement.progress.current / achievement.progress.target) * 100));
  };

  // Func»õie pentru formatarea datei √Æntr-un format plƒÉcut
  const formatDate = (dateString) => {
    if (!dateString) return '';
    
    const date = new Date(dateString);
    return date.toLocaleDateString('ro-RO', {
      day: 'numeric',
      month: 'long', 
      year: 'numeric'
    });
  };

  if (isLoading) {
    return (
      <div className={styles.loadingContainer}>
        <div className={styles.loadingSpinner}></div>
        <p>Se √ÆncarcƒÉ profilul...</p>
      </div>
    );
  }

  // Afi»ôƒÉm informa»õii de debugging c√¢nd avem erori
  if (error) {
    return (
      <div className={styles.errorContainer}>
        <h2>Eroare la √ÆncƒÉrcarea profilului</h2>
        <p className={styles.errorMessage}>{error}</p>
        
        {debugInfo && (
          <div className={styles.debugInfo}>
            <h3>Informa»õii de debugging:</h3>
            <pre>{JSON.stringify(debugInfo, null, 2)}</pre>
            <p>VerificƒÉ consola browserului pentru mai multe detalii.</p>
          </div>
        )}
        
        <div className={styles.actionButtons}>
          <button 
            className={styles.loginButton} 
            onClick={() => navigate('/login')}
          >
            Autentificare
          </button>
          <button 
            className={styles.returnButton} 
            onClick={() => navigate('/courses')}
          >
            √énapoi la Cursuri
          </button>
        </div>
      </div>
    );
  }

  // DacƒÉ nu avem date de utilizator valide, afi»ôƒÉm un mesaj »ôi oferim posibilitatea de autentificare
  if (!userInfo) {
    return (
      <div className={styles.errorContainer}>
        <h2>Nu s-au putut √ÆncƒÉrca datele profilului</h2>
        <p>Te rugƒÉm sƒÉ te autentifici din nou pentru a accesa profilul tƒÉu.</p>
        
        {debugInfo && (
          <div className={styles.debugInfo}>
            <h3>Informa»õii de debugging:</h3>
            <pre>{JSON.stringify(debugInfo, null, 2)}</pre>
          </div>
        )}
        
        <button 
          className={styles.loginButton} 
          onClick={() => navigate('/login')}
        >
          Autentificare
        </button>
      </div>
    );
  }

  // Calculare statistici totale
  const totalCourses = userProgress.length;
  const totalCompletedLessons = userProgress.reduce((total, course) => total + course.completedLessons, 0);
  
  // Calcularea scorului mediu (doar pentru testele sus»õinute)
  const takenTests = userProgress.filter(progress => progress.testScore !== null);
  const averageScore = takenTests.length > 0
    ? Math.round(takenTests.reduce((total, course) => total + course.testScore, 0) / takenTests.length)
    : 0;

  return (
    <div className={styles.profileContainer}>
      {/* Sec»õiunea header cu informa»õii utilizator »ôi statistici */}
      <div className={styles.profileHeader}>
        <div className={styles.userInfo}>
          <div className={styles.avatarContainer}>
            <img src="https://via.placeholder.com/150" alt="User Avatar" className={styles.userAvatar} />
            <div className={styles.avatarOverlay}>
              <button className={styles.changeAvatarBtn}>
                üì∑
              </button>
            </div>
          </div>
          
          <div className={styles.userDetails}>
            <h2 className={styles.username}>{userInfo?.username || 'Utilizator JavaVerse'}</h2>
            <p className={styles.email}>{userInfo?.email || 'email@example.com'}</p>
            <p className={styles.joinDate}>Membru din: {new Date(parseInt(userInfo._id.substring(0, 8), 16) * 1000).toLocaleDateString('ro-RO')}</p>
          </div>
        </div>

        {/* Statistici utilizator */}
        <div className={styles.statsContainer}>
          <div className={styles.statCard}>
            <div className={styles.statIcon}>
              üìö
            </div>
            <div className={styles.statInfo}>
              <span className={styles.statValue}>{totalCourses}</span>
              <span className={styles.statLabel}>Cursuri Active</span>
            </div>
          </div>
          
          <div className={styles.statCard}>
            <div className={styles.statIcon}>
              ‚úì
            </div>
            <div className={styles.statInfo}>
              <span className={styles.statValue}>{totalCompletedLessons}</span>
              <span className={styles.statLabel}>Lec»õii Finalizate</span>
            </div>
          </div>
          
          <div className={styles.statCard}>
            <div className={styles.statIcon}>
              üèÜ
            </div>
            <div className={styles.statInfo}>
              <span className={styles.statValue}>{averageScore}%</span>
              <span className={styles.statLabel}>Scor Mediu</span>
            </div>
          </div>
        </div>
      </div>

      {/* Afi»ôƒÉm informa»õii de debugging dacƒÉ sunt disponibile */}
      {debugInfo && (
        <div className={styles.debugPanel}>
          <details>
            <summary>Informa»õii de debugging (click pentru a deschide)</summary>
            <pre>{JSON.stringify(debugInfo, null, 2)}</pre>
            <p>User Info: {JSON.stringify(userInfo, null, 2)}</p>
            <p>Progress Items: {userProgress.length}</p>
          </details>
        </div>
      )}

      {/* Tab-uri pentru navigare √Æntre sec»õiuni */}
      <div className={styles.tabsContainer}>
        <button 
          className={`${styles.tabButton} ${activeTab === 'courses' ? styles.activeTab : ''}`}
          onClick={() => setActiveTab('courses')}
        >
          Cursurile Mele
        </button>
        <button 
          className={`${styles.tabButton} ${activeTab === 'achievements' ? styles.activeTab : ''}`}
          onClick={() => setActiveTab('achievements')}
        >
          RealizƒÉri
        </button>
        <button 
          className={`${styles.tabButton} ${activeTab === 'settings' ? styles.activeTab : ''}`}
          onClick={() => setActiveTab('settings')}
        >
          SetƒÉri
        </button>
      </div>

      {/* Con»õinut √Æn func»õie de tab-ul activ */}
      <div className={styles.tabContent}>
        {activeTab === 'courses' && (
          <div className={styles.courseGrid}>
            {userProgress.length > 0 ? (
              userProgress.map(progress => {
                const progressPercent = calculateProgress(progress.completedLessons, progress.totalLessons);
                return (
                  <div key={progress._id} className={styles.courseCard}>
                    <div className={styles.courseHeader}>
                      <h3 className={styles.courseName}>{progress.courseId.name}</h3>
                      <div className={styles.courseActions}>
                        <button 
