import React, { useState, useEffect } from 'react';

function Quiz({ courseId }) {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState([]);
  const [score, setScore] = useState(0);
  const [isFinished, setIsFinished] = useState(false);

  useEffect(() => {
    // Fetch întrebările pentru cursul curent
    fetch(`http://localhost:5000/api/courses/${courseId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.course && data.course.questions) {
          setQuestions(data.course.questions);  // Setăm întrebările
        }
      })
      .catch((error) => console.error('Error fetching questions:', error));
  }, [courseId]);

  const handleAnswerChange = (index, answerIndex) => {
    const updatedAnswers = [...selectedAnswers];
    updatedAnswers[index] = answerIndex;
    setSelectedAnswers(updatedAnswers);
  };

  const handleNext = () => {
    if (selectedAnswers[currentQuestionIndex] === undefined) {
      alert('Te rugăm să selectezi un răspuns!');
      return;
    }

    // Verificăm răspunsul
    if (selectedAnswers[currentQuestionIndex] === questions[currentQuestionIndex].correctAnswerIndex) {
      setScore(score + 1);
    }

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setIsFinished(true);  // Dacă testul s-a terminat
    }
  };

  const handleRestart = () => {
    setScore(0);
    setCurrentQuestionIndex(0);
    setSelectedAnswers([]);
    setIsFinished(false);
  };

  if (questions.length === 0) return <div>Loading questions...</div>;

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="quiz-container">
      <h2>{courseId} - Testul Cursului</h2>
      {!isFinished ? (
        <div className="question-container">
          <h3>{currentQuestion.question}</h3>
          <ul>
            {currentQuestion.options.map((option, index) => (
              <li key={index}>
                <input
                  type="radio"
                  name={`question-${currentQuestionIndex}`}
                  value={index}
                  checked={selectedAnswers[currentQuestionIndex] === index}
                  onChange={() => handleAnswerChange(currentQuestionIndex, index)}
                />
                <label>{option}</label>
              </li>
            ))}
          </ul>
          <button onClick={handleNext}>Next</button>
        </div>
      ) : (
        <div className="result-container">
          <h3>Testul S-a Încheiat</h3>
          <p>Punctajul tău: {score} din {questions.length}</p>
          <button onClick={handleRestart}>Începe din nou</button>
        </div>
      )}
    </div>
  );
}

export default Quiz;
