import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/CourseDetailPage.css';

function CourseDetailPage() {
  const { courseId, lessonId } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentLessonIndex, setCurrentLessonIndex] = useState(parseInt(lessonId) || 0); // lecția curentă
  const [isPreview, setIsPreview] = useState(true);  // Determină dacă suntem în previzualizarea cursului
  const [openLessonIndex, setOpenLessonIndex] = useState(null); // Indicele lecției deschise pentru preview

  useEffect(() => {
    fetch(`http://localhost:5000/api/courses/${courseId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.course) {
          setCourse(data.course);
          setLoading(false);
        }
      })
      .catch(() => setLoading(false));
  }, [courseId]);

  // Când dăm Start Course
  const handleStartCourse = () => {
    setIsPreview(false);  // După ce începe cursul, schimbăm starea
    navigate(`/courses/${courseId}/lesson/1`);  // Navigăm la lecția 1 (nu lecția 0)
  };

  // Când dăm Next Lesson
  const handleNextLesson = () => {
    if (currentLessonIndex < course.content.length - 1) {
      const nextLessonIndex = currentLessonIndex + 1;
      setCurrentLessonIndex(nextLessonIndex);
      navigate(`/courses/${courseId}/lesson/${nextLessonIndex}`);
    }
  };

  // Când dăm Back
  const handleBack = () => {
    if (isPreview) {
      // Dacă suntem în previzualizarea cursului, navigăm înapoi la lista de lecții
      navigate(`/courses/${courseId}`);  // Navigăm înapoi la pagina principală a cursului
    } else {
      // Dacă suntem într-o lecție, navigăm la lecția anterioară
      if (currentLessonIndex > 1) {
        setCurrentLessonIndex(currentLessonIndex - 1);
        navigate(`/courses/${courseId}/lesson/${currentLessonIndex - 1}`);
      } else if (currentLessonIndex === 1) {
        // Dacă suntem la lecția 1, navigăm înapoi la previzualizarea cursului
        setIsPreview(true);
        navigate(`/courses/${courseId}`);
      }
    }
  };

  const handleBackToCourses = () => {
    navigate('/courses');  // Navigăm la lista de cursuri
  };

  // Deschide/închide lecțiile pentru previzualizare
  const toggleLessonPreview = (index) => {
    setOpenLessonIndex(openLessonIndex === index ? null : index);
  };

  if (loading) return <div>Loading...</div>;
  if (!course) return <div>Course not found.</div>;

  const currentLesson = course.content[currentLessonIndex];

  return (
    <div className="course-detail-page">
      <h1 className="course-detail-title">{course.name}</h1>
      <p className="course-detail-description">{course.description}</p>

      {isPreview ? (
        <>
          <h3 className="course-detail-content-title">Course Content:</h3>
          <div className="course-preview">
            {course.content.map((lesson, index) => (
              <div key={index} className="course-preview-item">
                <div onClick={() => toggleLessonPreview(index)} className="lesson-title">
                  {lesson.title}
                </div>
                {openLessonIndex === index && (
                  <div className="lesson-preview">
                    <p>{lesson.content.slice(0, 100)}...</p> {/* Afișează doar un preview scurt */}
                  </div>
                )}
              </div>
            ))}
          </div>
          <div className="course-buttons">
            <button className="back-to-courses-btn" onClick={handleBackToCourses}>
              Back to Courses
            </button>
            <button className="start-course-btn" onClick={handleStartCourse}>
              Start Course
            </button>
          </div>
        </>
      ) : (
        <>
          <h3 className="course-detail-content-title">Course Content:</h3>
          <div className="course-detail-content">
            <h4>{currentLesson.title}</h4>
            <p>{currentLesson.content}</p>
            <div className="course-lesson-text">
              {currentLesson.examples.map((example, index) => (
                <div key={index}>
                  <pre>{example.code}</pre>
                  <p>{example.explanation}</p>
                </div>
              ))}
            </div>
          </div>
          <div className="course-buttons">
            <button className="back-btn" onClick={handleBack}>
              Back
            </button>

            {currentLessonIndex < course.content.length - 1 ? (
              <button className="next-lesson-btn" onClick={handleNextLesson}>
                Next Lesson
              </button>
            ) : (
              <p>You've completed all lessons in this course!</p>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default CourseDetailPage;
