import React, { useState, useEffect } from 'react';

function Profile() {
  const [userProgress, setUserProgress] = useState([]);

  useEffect(() => {
    // Fetch progresul utilizatorului
    fetch('/api/user/progress/USER_ID') // Înlocuiește cu ID-ul utilizatorului real
      .then(response => response.json())
      .then(data => {
        console.log("Progresul utilizatorului:", data);  // Adăugăm un console log pentru debugging
        setUserProgress(data);
      })
      .catch(error => console.error('Error fetching user progress:', error));
  }, []);

  const calculateProgress = (completedLessons, totalLessons) => {
    return (completedLessons / totalLessons) * 100;
  };

  return (
    <div className="profile-container">
      <h2>Profilul Utilizatorului</h2>
      <div className="course-list">
        {userProgress.length > 0 ? (
          userProgress.map(progress => (
            <div key={progress._id} className="course">
              <h3>{progress.courseId.name}</h3>
              <div className="progress-bar-container">
                <div
                  className="progress-bar"
                  style={{
                    width: `${calculateProgress(progress.completedLessons, progress.totalLessons)}%`,
                  }}
                ></div>
              </div>
              <p>
                Lecții finalizate: {progress.completedLessons} din {progress.totalLessons}
              </p>
              {progress.testScore !== null ? (
                <p>Testul: {progress.testScore} din 100</p>
              ) : (
                <p>Test nesustinut</p>
              )}
            </div>
          ))
        ) : (
          <p>Încă nu ai progres la niciun curs.</p> // Mesaj pentru cazurile în care nu există date
        )}
      </div>
    </div>
  );
}

export default Profile;
