// Modificarea principală în CodeEditor.js
// Înlocuiește logica de încărcare Monaco Editor cu aceasta:

import React, { useState, useEffect, useRef } from 'react';
import { useToast } from './ToastProvider';
import '../styles/CodeEditor.css';
import Button from './Button';

function CodeEditor({ initialCode, lessonId }) {
  const defaultCode = `public class Main {
    public static void main(String[] args) {
        // Scrie codul tău aici
        System.out.println("Bine ai venit la JavaVerse!");
    }
}`;

  const [code, setCode] = useState(initialCode || defaultCode);
  const [output, setOutput] = useState('');
  const [isRunning, setIsRunning] = useState(false);
  const [isEditorReady, setIsEditorReady] = useState(false);
  const [isEditorLoading, setIsEditorLoading] = useState(true);
  const [feedback, setFeedback] = useState('');
  const [showFeedback, setShowFeedback] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [lastInitialCode, setLastInitialCode] = useState(initialCode); // Pentru a urmări schimbările
  const monacoRef = useRef(null);
  const editorRef = useRef(null);
  const toast = useToast();

  // Inițializarea Monaco Editor - îmbunătățită
  useEffect(() => {
    let isMounted = true;
    
    const loadMonacoEditor = async () => {
      try {
        setIsEditorLoading(true);
        
        // Importăm Monaco Editor
        const monaco = await import('@monaco-editor/react');
        
        if (isMounted) {
          monacoRef.current = monaco;
          setIsEditorLoading(false);
          
          // Setăm un mic delay pentru a permite inițializarea completă
          setTimeout(() => {
            if (isMounted) {
              setIsEditorReady(true);
            }
          }, 100);
        }
      } catch (error) {
        console.error('Eroare la încărcarea Monaco Editor:', error);
        if (isMounted) {
          setIsEditorLoading(false);
          toast.error('Nu s-a putut încărca editorul de cod.');
        }
      }
    };

    loadMonacoEditor();
    
    return () => {
      isMounted = false;
      if (editorRef.current) {
        editorRef.current.dispose();
      }
    };
  }, [toast]);

  // Actualizăm codul când se modifică initialCode (doar pentru schimbări externe)
  useEffect(() => {
    // Verificăm dacă initialCode s-a schimbat cu adevărat (nu din cauza re-render-ului)
    if (initialCode && initialCode !== lastInitialCode && initialCode.trim() !== '') {
      console.log('Actualizăm editorul cu cod nou:', initialCode.substring(0, 50) + '...');
      setCode(initialCode);
      setLastInitialCode(initialCode);
      
      // Dacă editorul este deja încărcat, actualizăm valoarea direct
      if (editorRef.current && isEditorReady) {
        updateEditorCode(initialCode);
      }
    }
  }, [initialCode, lastInitialCode, isEditorReady]);

  const handleEditorDidMount = (editor) => {
    editorRef.current = editor;
    setIsEditorReady(true);
    setIsEditorLoading(false);
    
    // Focalizăm editorul pentru o experiență mai bună
    editor.focus();
    
    // Dacă avem cod inițial, îl setăm
    if (initialCode) {
      editor.setValue(initialCode);
    }
    
    console.log('Monaco Editor s-a încărcat cu succes');
  };

  // Funcție pentru actualizarea smooth a codului în editor
  const updateEditorCode = (newCode) => {
    if (editorRef.current && isEditorReady && newCode) {
      const editor = editorRef.current;
      const currentValue = editor.getValue();
      
      if (currentValue !== newCode) {
        console.log('Actualizez codul în editor...');
        // Salvăm poziția cursorului
        const position = editor.getPosition();
        
        // Actualizăm codul
        editor.setValue(newCode);
        
        // Restaurăm poziția cursorului (dacă este validă)
        if (position && newCode.split('\n').length >= position.lineNumber) {
          editor.setPosition(position);
        } else {
          // Plasăm cursorul la începutul codului
          editor.setPosition({ lineNumber: 1, column: 1 });
        }
      }
    }
  };

  // Handler pentru schimbările în editor (de la utilizator)
  const handleCodeChange = (newCode) => {
    console.log('Utilizatorul a modificat codul');
    setCode(newCode);
  };

  const handleRunCode = async () => {
    if (!isEditorReady) {
      toast.warning('Editorul nu este pregătit încă. Te rugăm să aștepți.');
      return;
    }

    setIsRunning(true);
    setOutput('Se execută codul...');

    try {
      // Apelul către API-ul nostru backend
      const response = await fetch('http://localhost:5000/api/execute-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${sessionStorage.getItem('authToken')}`
        },
        body: JSON.stringify({
          code: code
        }),
      });

      const data = await response.json();
      
      if (data.error) {
        setOutput(`Eroare: ${data.error}`);
        toast.error('A apărut o eroare la executarea codului.');
      } else {
        setOutput(data.output);
        if (data.statusCode === 200) {
          toast.success('Codul a fost executat cu succes!');
        }
      }
    } catch (error) {
      console.error('Error running code:', error);
      
      // Dacă apelul API eșuează, revenim la simulare pentru dezvoltare
      handleRunCodeSimulation();
    } finally {
      setIsRunning(false);
    }
  };

  // Simulare a rulării codului (pentru dezvoltare fără API)
  const handleRunCodeSimulation = () => {
    setIsRunning(true);
    setOutput('Se execută codul...');

    // Simulăm un delay pentru execuție
    setTimeout(() => {
      if (code.includes('System.out.println')) {
        // Extragem mesajul din println folosind regex
        const outputMatches = code.match(/System\.out\.println\("(.*)"\)/g);
        if (outputMatches && outputMatches.length > 0) {
          // Pentru multiple println-uri
          const outputs = outputMatches.map(match => {
            const content = match.match(/System\.out\.println\("(.*)"\)/);
            return content && content[1] ? content[1] : '';
          });
          setOutput(outputs.join('\n'));
          toast.success('Codul a fost executat cu succes!');
        } else {
          setOutput('Bine ai venit la JavaVerse!');
          toast.success('Codul a fost executat cu succes!');
        }
      } else if (code.includes('error') || code.includes('Error')) {
        setOutput('Exception in thread "main" java.lang.RuntimeException: Simulare eroare\n   at Main.main(Main.java:4)');
        toast.error('Codul conține erori de compilare.');
      } else {
        setOutput('Execuție reușită! (fără output)');
        toast.success('Codul a fost executat cu succes!');
      }
      setIsRunning(false);
    }, 1500);
  };

  // Funcție pentru a obține feedback AI pentru cod
  const handleGetFeedback = async () => {
    setIsAnalyzing(true);
    setFeedback('Se analizează codul...');
    setShowFeedback(true);

    try {
      const response = await fetch('http://localhost:5000/api/analyze-code', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${sessionStorage.getItem('authToken')}`
        },
        body: JSON.stringify({
          code: code,
          lessonId: lessonId
        }),
      });

      const data = await response.json();
      
      if (data.success) {
        setFeedback(data.feedback);
        toast.success('Feedback generat cu succes!');
      } else {
        setFeedback('Nu am putut genera feedback pentru acest cod.');
        toast.error('Eroare la generarea feedback-ului.');
      }
    } catch (error) {
      console.error('Error analyzing code:', error);
      
      // Dacă apelul API eșuează, generăm feedback simplu pentru dezvoltare
      setTimeout(() => {
        const simulatedFeedback = `# Analiza codului tău

Salut! Iată feedback-ul meu pentru codul tău Java:

## Puncte forte:
- Structura de bază a programului este corectă
- Folosești corect metoda System.out.println()

## Sugestii de îmbunătățire:
- Adaugă comentarii pentru a explica ce face codul tău
- Consideră folosirea mai multor metode pentru a separa logica

Continuă să exersezi și să îmbunătățești codul!`;
        
        setFeedback(simulatedFeedback);
        setIsAnalyzing(false);
        toast.success('Feedback generat cu succes!');
      }, 2000);
    } finally {
      setIsAnalyzing(false);
    }
  };

  // Resetează editorul la codul inițial
  const handleResetCode = () => {
    if (window.confirm('Sigur dorești să resetezi codul? Toate modificările vor fi pierdute.')) {
      setCode(initialCode || defaultCode);
      toast.info('Codul a fost resetat.');
    }
  };

  return (
    <div className="code-editor-container">
      <div className="editor-header">
        <h3>Editor de cod Java</h3>
        <div className="editor-actions">
          <Button 
            variant="secondary"
            size="small"
            onClick={handleResetCode}
            icon="🔄"
            iconPosition="left"
          >
            Resetează
          </Button>
          <Button 
            variant="info"
            size="small"
            onClick={handleGetFeedback}
            disabled={isAnalyzing || !isEditorReady}
            icon={isAnalyzing ? "⏳" : "🤖"}
            iconPosition="left"
          >
            {isAnalyzing ? 'Se analizează...' : 'Obține feedback AI'}
          </Button>
          <Button 
            variant="primary"
            size="small"
            onClick={handleRunCode}
            disabled={isRunning || !isEditorReady}
            icon={isRunning ? "⏳" : "▶️"}
            iconPosition="left"
          >
            {isRunning ? 'Se execută...' : 'Rulează codul'}
          </Button>
        </div>
      </div>

      <div className="editor-wrapper">
        {isEditorLoading ? (
          <div className="editor-loading">
            <div className="loading-spinner"></div>
            <p>Se încarcă editorul...</p>
          </div>
        ) : monacoRef.current && isEditorReady ? (
          <monacoRef.current.default
            height="400px"
            language="java"
            theme="vs-dark"
            value={code}
            onChange={handleCodeChange}
            onMount={handleEditorDidMount}
            options={{
              minimap: { enabled: false },
              fontSize: 14,
              scrollBeyondLastLine: false,
              automaticLayout: true,
              tabSize: 2,
              folding: true,
              lineNumbers: 'on',
              renderLineHighlight: 'all',
              wordWrap: 'on',
              formatOnPaste: true,
              formatOnType: true
            }}
          />
        ) : (
          <div className="editor-fallback">
            <p>Nu s-a putut încărca editorul Monaco.</p>
            <textarea
              value={code}
              onChange={(e) => handleCodeChange(e.target.value)}
              className="fallback-editor"
              rows="20"
              placeholder="Scrie codul tău Java aici..."
            />
          </div>
        )}
      </div>

      <div className="output-container">
        <div className="output-header">
          <h4>Rezultat</h4>
        </div>
        <pre className="output-content">{output}</pre>
      </div>

      {showFeedback && (
        <div className="feedback-container">
          <div className="feedback-header">
            <h4>Feedback AI</h4>
            <button 
              className="close-feedback" 
              onClick={() => setShowFeedback(false)}
              aria-label="Închide feedback"
            >
              &times;
            </button>
          </div>
          <div className="feedback-content">
            {feedback}
          </div>
        </div>
      )}
    </div>
  );
}

export default CodeEditor;
